window.onscroll=function(){scroll()};function scroll(){if(document.body.scrollTop>20||document.documentElement.scrollTop>20){document.getElementById("top").style.display="block";}else{document.getElementById("top").style.display="none";}}
function scrollToTop(){if(document.body.scrollTop!==0||document.documentElement.scrollTop!==0){window.scrollBy(0,-30);requestAnimationFrame(scrollToTop);}}
